module.exports = function(gulp, options, plugins) {
    gulp.task('post-build', function(done) {
        var file = plugins.path.join(options.dest, 'jibo-keyframe-info.js');
        var content = 'module.exports = require("../").JiboKeyframeInfo;';
        plugins.fs.writeFile(file, content, done);
    });
};