var epsilon = require("./utils/epsilon"),
    tweening = require("../../lib/tweening.js");

describe('keyframes.tweening', function(){
    describe('.number()', function(){
        it('should return first value when time = 0.0', function(){
            epsilon.equal(tweening.rotation(1.3, 9.2, 0.0, "linear"), 1.3);
        });
        it('should return second value when time = 1.0', function(){
            epsilon.equal(tweening.rotation(1.3, 9.2, 1.0, "linear"), 9.2);
        });
        it('should return average of values when time = 0.5', function(){
            epsilon.equal(tweening.rotation(2.0, 4.0, 0.5, "linear"), 3.0);
        });
    });
});
