/**
 * test/util/epsilon.js - Provides comparison functions that compensate for the
 * precision limitations of floating numbers.
 */
var assert = require("assert"),
    epsilonValue = 0.0001;

module.exports = {

    /**
     * equal - Compare two numbers and check if they are equal within a distance of epsilon
     *
     * @param  {number} a Value to compare against b
     * @param  {number} b Value to compare against a
     *
     * Throws an error if they are not equal
     */
    equal: function(a, b, epsilon, errorMessage){
        assert(a > (b - epsilonValue) && a < (b + epsilonValue), errorMessage);
    }
};
